requestGroovy:
import com.alipay.muagent.model.connector.http.HttpParameters
import com.google.gson.Gson

class Content {
    String text;
}

def convertRequest(String request) {
    def gson = new Gson()

    Content content = new Content()
    content.text = request

    HttpParameters parameters = HttpParameters.builder().build()
    parameters.setRequestBody(gson.toJson(content))
    return gson.toJson(parameters)
}

convertRequest(request)

responseGroovy:
import com.google.gson.Gson

class LLMResponse {

    int successCode;

    String errorMessage;

    String answer;
}

def convertResponse(String response) {
    def gson = new Gson()
    LLMResponse lr = gson.fromJson(response, LLMResponse.class)

    if (lr.getSuccessCode() == 1) {
        return lr.getAnswer()
    }

    throw new RuntimeException(lr.getErrorMessage())
}

convertResponse(response)
