import com.google.gson.Gson

class LLMResponse {

    int successCode;

    String errorMessage;

    String answer;
}

def convertResponse(String response) {
    def gson = new Gson()
    LLMResponse lr = gson.fromJson(response, LLMResponse.class)

    if (lr.getSuccessCode() == 1) {
        return lr.getAnswer()
    }

    throw new RuntimeException(lr.getErrorMessage())
}

