def randRole() {
    def words = Arrays.asList(
            ["苹果", "梨"],
            ["猫", "狗"],
            ["摩托车", "自行车"],
            ["太阳", "月亮"],
            ["红色", "粉色"],
            ["大象", "长颈鹿"],
            ["铅笔", "钢笔"],
            ["牛奶", "豆浆"],
            ["河", "湖"],
            ["面包", "蛋糕"],
            ["饺子", "包子"],
            ["冬天", "夏天"],
            ["电视", "电脑"],
            ["铅笔", "橡皮"],
            ["跑步", "游泳"],
            ["手机", "平板"],
            ["鱼", "虾"],
            ["空调", "风扇"],
            ["马", "驴"],
            ["书", "杂志"],
            ["草", "树"],
            ["杯子", "碗"],
            ["米饭", "面条"],
            ["饼干", "蛋糕"],
            ["雨伞", "雨衣"],
            ["猪", "牛"],
            ["白菜", "生菜"],
            ["吉他", "钢琴"],
            ["飞机", "火车"],
            ["镜子", "眼镜"]
    )
    def player = [["张伟", "agent_张伟"], [ "李静", "agent_李静"], ["王鹏", "agent_王鹏"], ["人类玩家", "agent_人类玩家"]]
    player.shuffle()
    def role = Arrays.asList("平民_1", "平民_2", "平民_3", "卧底_1")
    role.shuffle()
    def random = new Random()
    def wordIdx = random.nextInt(65536) % words.size()
    def underCoverWordIdx = random.nextInt(65536) % 2
    return (0..3).collect { i ->
        {
            def r = new HashMap<String, Object>()
            r.put("player_name", player.get(i).get(0))
            r.put("agent_name", player.get(i).get(1))
            r.put("agent_description", role.get(i))
            r.put("单词", role.get(i).startsWith("平民") ? words.get(wordIdx).get(1 - underCoverWordIdx) : words.get(wordIdx).get(underCoverWordIdx))
            return r
        }
    }
}

def convertRequest(String request) {
    def gson = new Gson()
    def roles = randRole()
    return gson.toJson(roles)
}

convertRequest(request)

